import React, { Component } from 'react'


class Form extends Component {
    // copied and pasted state and moving it to App component
    // because 
    // state = {
    //     inputValue: ''
    // }

    // handleChange is no longer working because it is trying 
    // to change Form.js component 
    // so it needs to move to the parent (App.js component)
    // handleChange = (e) => {
    //     console.log(e.target.value);
    //     this.setState({ inputValue: e.target.value });
    // }

    render() {
        return (
            <div className="Form">
                <form>
                    <input
                        onChange={(e) => this.handleChange(e)}
                        // value={this.state.inputValue}
                        // from "state" it changes "props" because App.js component
                        // needs to access by passing (inputValue) 
                        // to Form.js component by usig "props"
                        value={this.props.inputValue}
                    />
                </form>
            </div>
        );
    }
}

export default Form;
