import React, { Component } from 'react';
import './App.css';

import Form from './components/Form';
import List from './components/List';

class App extends Component {
  // * it is passing the value to Form.js component
  state = {
    inputValue: '',
    todos: [
      { value: "From List passing to Task 1", done: false },
      { value: "From List mapping to Task 2", done: true }
    ]
  }
  // * moved from Form.js component
  handleChange = (e) => {
    console.log(e.target.value);
    this.setState({ inputValue: e.target.value });
  }

  render() {
    return (
      <div className="App">
        <Form
          handleChange={this.handleChange}
          // inputValue needs to pass the value to Form.js component
          // todo -- using "state" and "props"
          inputValue={this.state.inputValue}
        />
        <List 
          todos={this.state.toods}
        />
      </div>
    );
  }
}

export default App;
