import React, { Component } from 'react'

import Task from './Task'

class List extends Component {

    state = {
        // * moved to App.js
        todos: [
            { value: "From List passing to Task 1", done: false },
            { value: "From List mapping to Task 2", done: true }
        ]
    }

    render() {
        return (
            <div className="List">
                {this.state.todos.map((todo, i) => {
                    return (
                        <Task
                            key={i}
                            passingToProps={todo}
                        />
                    )
                })}
            </div>
        )
    }
}

export default List;
