import React, { Component } from 'react';
import './App.css';

import Form from './components/Form';
import List from './components/List';

class App extends Component {
  // * it is passing the value to Form.js component
  state = {
    inputValue: '',
    todosFromList: [
      { value: "From App to List passing to Task 1", done: false },
      { value: "From App to List mapping to Task 2", done: true }
    ]
  }
  // * moved from Form.js component
  handleChange = (e) => {
    // console.log(e.target.value);
    this.setState({ inputValue: e.target.value });
  }

  handleSubmit = (e) => {
    // * it's a method that keep the page from rerendering
    e.preventDefault();
    console.log('YES!!!');
  }

  render() {
    return (
      <div className="App">
        <Form
          // inputValue needs to pass the value to Form.js component
          // todo -- using "state" to pass a function using "props" to Form.js component
          inputValue={this.state.inputValue}
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
        />
        <List 
          todosPassToList={this.state.todosFromList}
        />
      </div>
    );
  }
}

export default App;
